import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import emailjs from '@emailjs/browser';

const WaitlistSignUp = () => {
  const navigate = useNavigate();
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleBack = () => {
    navigate('/');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!firstName || !lastName || !email) {
      alert('Please fill in all fields');
      return;
    }

    setIsSubmitting(true);

    // EmailJS configuration - You'll need to set up EmailJS and add your keys here
    // For now, using mailto as a fallback
    const subject = encodeURIComponent("You're on the Spark Waitlist! ‚ö°");
    const body = encodeURIComponent(`Hi there,

Thanks for joining the waitlist for Spark, Western Developer Society's 24-hour hackathon and case competition. Happening at Western University from January 30th - February 1st 2026. 

You're all set. As soon as applications open, you'll be the first to know. Keep an eye on your inbox for updates, key dates, and everything you'll need to get ready for Spark.

If you have any questions in the meantime, feel free to reply to this email.

Can't wait to share more soon, get ready to create something big. üî•‚ö°

Best,

WDS Externals Team`);

    // Using mailto link to open email client
    // Note: For automatic email sending, you'll need to set up EmailJS or a backend service
    window.location.href = `mailto:${email}?subject=${subject}&body=${body}&from=ryhana.williams0@gmail.com`;
    
    // Alternative: Use EmailJS for automatic sending (requires setup)
    // Uncomment and configure when EmailJS is set up:
    /*
    try {
      await emailjs.send(
        'YOUR_SERVICE_ID',
        'YOUR_TEMPLATE_ID',
        {
          to_email: email,
          to_name: `${firstName} ${lastName}`,
          from_email: 'ryhana.williams0@gmail.com',
          subject: "You're on the Spark Waitlist! ‚ö°",
          message: body
        },
        'YOUR_PUBLIC_KEY'
      );
      alert('Successfully joined the waitlist! Check your email.');
    } catch (error) {
      console.error('Email sending failed:', error);
      alert('There was an error sending the email. Please try again.');
    }
    */
    
    setIsSubmitting(false);
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      {/* Background Image - Fixed, no scrolling */}
      <img 
        src="/assets/waitlist sign up.png" 
        alt="Waitlist Sign Up Background"
        className="absolute inset-0 w-full h-full object-cover"
      />
      
      {/* Back Button - Top Right Corner */}
      <button
        onClick={handleBack}
        className="absolute top-8 right-8 z-50 bg-white bg-opacity-80 hover:bg-opacity-100 text-dark-green font-metropolis font-bold px-6 py-3 rounded-lg shadow-lg transition-all duration-200 hover:scale-105"
      >
        ‚Üê Back
      </button>

      {/* White Form Card with Shadow */}
      <div className="absolute inset-0 flex items-center justify-center z-40">
        <div className="bg-white rounded-lg shadow-2xl p-8 w-full max-w-md mx-4">
          <form className="space-y-6">
            {/* First Name Input */}
            <div>
              <label htmlFor="firstName" className="block text-dark-green font-metropolis font-semibold mb-2">
                First Name
              </label>
              <input
                type="text"
                id="firstName"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:outline-none focus:border-dark-green font-metropolis text-gray-800"
                placeholder="Enter your first name"
              />
            </div>

            {/* Last Name Input */}
            <div>
              <label htmlFor="lastName" className="block text-dark-green font-metropolis font-semibold mb-2">
                Last Name
              </label>
              <input
                type="text"
                id="lastName"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:outline-none focus:border-dark-green font-metropolis text-gray-800"
                placeholder="Enter your last name"
              />
            </div>

            {/* Email Input */}
            <div>
              <label htmlFor="email" className="block text-dark-green font-metropolis font-semibold mb-2">
                Email
              </label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-lg focus:outline-none focus:border-dark-green font-metropolis text-gray-800"
                placeholder="Enter your email"
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default WaitlistSignUp;
