import React, { useState } from 'react';

const Hero = () => {
  const [activeTab, setActiveTab] = useState('HOME');

  const navItems = ['HOME', 'ABOUT', 'TESTIMONIALS', 'SPONSORS'];

  return (
    <section className="relative min-h-screen bg-gradient-to-br from-hero-start to-hero-end">
      {/* Header positioned on top of the gradient */}
      <header className="absolute top-0 left-0 right-0 z-10 px-8 py-4 flex justify-between items-center">
        <div className="flex items-center space-x-8">
          <img 
            src="/assets/WDS Logo.png" 
            alt="WDS Logo" 
            className="h-8 w-auto"
          />
          
          {/* Navigation Menu */}
          <nav className="flex space-x-12">
            {navItems.map((item) => (
              <a 
                key={item}
                href={`#${item.toLowerCase()}`} 
                className={`text-white font-metropolis text-lg transition-all duration-200 hover:opacity-80 ${
                  activeTab === item ? 'font-bold' : 'font-thin'
                }`}
                onClick={() => setActiveTab(item)}
              >
                {item}
              </a>
            ))}
          </nav>
        </div>
      </header>
      
      {/* Stars in upper half */}
      <div className="absolute inset-0 z-5">
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-16 left-20 w-4 h-4 opacity-70 animate-bounce"
          style={{ animationDuration: '3s', animationDelay: '0s' }}
        />
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-24 right-32 w-3 h-3 opacity-60 animate-bounce"
          style={{ animationDuration: '2.5s', animationDelay: '0.5s' }}
        />
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-32 left-40 w-2 h-2 opacity-80 animate-bounce"
          style={{ animationDuration: '4s', animationDelay: '1s' }}
        />
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-20 right-60 w-3 h-3 opacity-75 animate-bounce"
          style={{ animationDuration: '3.5s', animationDelay: '1.5s' }}
        />
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-40 left-60 w-2 h-2 opacity-65 animate-bounce"
          style={{ animationDuration: '2.8s', animationDelay: '2s' }}
        />
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-28 right-80 w-4 h-4 opacity-70 animate-bounce"
          style={{ animationDuration: '3.2s', animationDelay: '0.8s' }}
        />
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-36 left-80 w-3 h-3 opacity-60 animate-bounce"
          style={{ animationDuration: '4.2s', animationDelay: '1.2s' }}
        />
        <img 
          src="/assets/Star.png" 
          alt="Star" 
          className="absolute top-44 right-40 w-2 h-2 opacity-85 animate-bounce"
          style={{ animationDuration: '2.2s', animationDelay: '1.8s' }}
        />
      </div>
      
      {/* Blue gradient background */}
    </section>
  );
};

export default Hero;
